import React, { useState } from 'react';
import {
    Banner,
    Card,
    DisplayText,
    Form,
    FormLayout,
    Frame,
    Layout,
    Page,
    PageActions,
    TextField,
    Toast,
    Heading
} from '@shopify/polaris';
import store from 'store-js';
import gql from 'graphql-tag';


import { Mutation, useQuery } from 'react-apollo';
import { Loading } from '@shopify/app-bridge/actions';
import { array } from 'prop-types';
import { nonExecutableDefinitionMessage } from 'graphql/validation/rules/ExecutableDefinitions';




const UPDATE_METAFIELD = gql`
mutation($input: ProductInput!) {
    productUpdate(input: $input) {
      product {
        metafields(first: 100) {
          edges {
            node {
              id
              namespace
              key
              value
            }
          }
        }
      }
    }
  }`



export default function EditProduct() {

    const [metafield, setMetafield] = useState([])

    // const item = store.get('item');
    // let mf = [];
    // if (item != undefined) {
    //     for (let i = 0; i < item.metafields.edges.length; i++) {

    //         mf[item.metafields.edges[i].node.key] = item.metafields.edges[i].node.value;

    //         console.log('MF', mf);
    //     }
    //     setMetafield(mf);
    // }

    // console.log('MF->', mf);
    // console.log('ITEM->', item);
    // store.each(function (value, key) {
    //     if (key == 'item') {
    //         // setMetafield({ ...metafield, key })
    //         const tmpvalue = value.metafields.edges.reduce((r, e) => {
    //             r[e.node.key] = e.node.value
    //             return r;
    //         });
    //         console.log('tmp', tmpvalue);
    //         // setMetafield(metafield, tmp);
    //     }
    //     // console.log(key, '==', value)
    // })
    // const { loading, error, data } = useQuery(GET_METAFIELD, {
    //     variables: { productid: `gid://shopify/Product/${item.id}` }
    // })

    // console.log(item != undefined && item.metafields)
    // const reworkMetafields = item != undefined && item.metafields.edges.reduce((r, e) => {
    //     let key = e.node.key;
    //     // setMetafield({ ...metafield, [key]: e.node.value })
    //     r[key] = e.node
    //     return r;
    // }, {});

    // Array.isArray(reworkMetafields) && reworkMetafields.map(e => console.log('element', e));

    useEffect(() => {
        const item = store.get('item')

        for (let i = 0; i < item.metafields.egdes; i++) {
            let field = item.metafields.egdes[i].node.key;
            setMetafield({ ...metafields, [field]: item.metafields.egdes[i].node.value });
            setMetafield({ ...metafields, [field + 'id']: item.metafields.egdes[i].node.id });
        }
    })

    const handleChange = name => event => {
        setMetafield({ ...metafield, [name]: event });
    }

    const getValue = name => {
        if (item != undefined) {
            console.log('getValue', name);
            return item.metafields.edges.find(e => e.node.key == name).node.value;
        }
        return '';
    }

    // console.log('rework', reworkMetafields)

    return (
        <Mutation
            mutation={UPDATE_METAFIELD}
        >
            {(handleSubmit, { error, data }) => {
                console.log('DATA', data)
                const showError = error && (
                    <Banner status="critical">{error.message}</Banner>
                );
                const showToast = data && data.productUpdate && (
                    <Toast
                        content="Sucessfully updated"
                    // onDismiss={() => this.setState({ showToast: false })}
                    />
                );

                return (
                    <Frame>
                        <Page>
                            <Layout>

                                {showToast}

                                <Layout.Section>
                                    <DisplayText size='extraLarge'>Section 1 for {item != undefined && item.title}</DisplayText>
                                    <DisplayText size="small">The Header of your product pages is the most important section as it’s the first thing a visitor sees. You have less than 3 seconds to get their attention to read more. You have to make it count!</DisplayText>
                                    <DisplayText size="small">Every product solves a problem. You need to identify what problem the product solves. Then you need to identify what someone is feeling who has this problem. (Sad, annoyed, frustrated, jealous)</DisplayText>
                                    <DisplayText size="small">Then you need the visitor to realize you solve that problem they have.</DisplayText>
                                    <DisplayText size="small">This is done in the headline. Asking if someone has that problem with a question is one of the best ways to hit the pain point someone has who needs your product.</DisplayText>
                                    {showError}
                                </Layout.Section>
                                <Layout.Section>


                                    <Form>
                                        <Card sectioned>
                                            <FormLayout>

                                                <FormLayout.Group>
                                                    <TextField
                                                        prefix=""
                                                        value={getValue('headline')}
                                                        onChange={handleChange('headline')}

                                                        label="What is a question you could ask someone?"
                                                        type="text"
                                                        helpText="Examples: Have dandruff? Feeling tired or sluggish? Sick Of Zits? Got Back Pain?"

                                                    />
                                                </FormLayout.Group>
                                                <FormLayout.Group>
                                                    <TextField
                                                        prefix=""
                                                        value={metafield.outcome || ''}
                                                        onChange={handleChange('outcome')}
                                                        label="In one sentence describe the outcome of using your product."
                                                        type="price"
                                                        helpText="Examples:  “Look and feel your best in less than 24 hours!” “Grow the beard of your dreams.” “Say goodbye to pain without needing surgery.”"

                                                    />
                                                </FormLayout.Group>

                                                <FormLayout.Group>
                                                    <TextField
                                                        prefix=""
                                                        value={metafield.videoLink || ''}
                                                        onChange={handleChange('videoLink')}

                                                        label="Do you have a YouTube video selling the product?"
                                                        type="price"
                                                        helpText="If not, leave blank."
                                                    />
                                                </FormLayout.Group>

                                            </FormLayout>
                                        </Card>
                                        <PageActions
                                            primaryAction={[
                                                {
                                                    content: 'Next',
                                                    onAction: () => {
                                                        console.log('METAFIELDS', item.metafields)
                                                        console.log(item.metafields.edges[0])
                                                        const productMetafield = {

                                                            "id": item.id,
                                                            "metafields": [
                                                                {
                                                                    'namespace': 'vizi',
                                                                    "key": "headline",
                                                                    "value": metafield.headline,
                                                                    "valueType": "STRING"
                                                                },
                                                                {
                                                                    'namespace': 'vizi',
                                                                    "key": "outcome",
                                                                    "value": metafield.outcome,
                                                                    "valueType": "STRING"
                                                                },
                                                                {
                                                                    'namespace': 'vizi',
                                                                    "key": "videolink",
                                                                    "value": metafield.videoLink,
                                                                    "valueType": "STRING"
                                                                }
                                                            ]

                                                        };
                                                        {
                                                            handleSubmit({
                                                                variables: { input: productMetafield },
                                                            })
                                                        };
                                                    }
                                                }
                                            ]}
                                        />
                                    </Form>
                                </Layout.Section>
                            </Layout>
                        </Page>
                    </Frame>
                );
            }}
        </Mutation>
    );


    // handleChange = (field) => {
    //     return (value) => this.setState({ [field]: value });
    // };

    // itemToBeConsumed = () => {
    //     const item = store.get('item');
    //     const price = item.variants.edges[0].node.price;
    //     const variantId = item.variants.edges[0].node.id;
    //     const discounter = price * 0.1;
    //     this.setState({ price, variantId });
    //     return (price - discounter).toFixed(2);
    // };
}
