import {
    Banner,
    Card,
    DisplayText,
    Form,
    FormLayout,
    Frame,
    Layout,
    Page,
    PageActions,
    TextField,
    Toast,
} from '@shopify/polaris';
import store from 'store-js';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

const UPDATE_PRICE = gql`
  mutation productVariantUpdate($input: ProductVariantInput!) {
    productVariantUpdate(input: $input) {
      product {
        title
        description
      }
      productVariant {
        id
        price
      }
    }
  }
`;

class EditProduct extends React.Component {
    state = {
        discount: '',
        description: '',
        price: '',
        variantId: '',
        showToast: false,
    };

    componentDidMount() {
        this.setState({ discount: this.itemToBeConsumed() });
    }

    render() {
        const { name, description, price, discount, variantId } = this.state;
        return (
            <Mutation
                mutation={UPDATE_PRICE}
            >
                {(handleSubmit, { error, data }) => {
                    const showError = error && (
                        <Banner status="critical">{error.message}</Banner>
                    );
                    const showToast = data && data.productVariantUpdate && (
                        <Toast
                            content="Sucessfully updated"
                            onDismiss={() => this.setState({ showToast: false })}
                        />
                    );

                    return (
                        <Frame>
                            <Page>
                                <Layout>
                                    {showToast}
                                    <Layout.Section>
                                        {showError}
                                    </Layout.Section>
                                    <Layout.Section>
                                        <DisplayText size="large">{name}</DisplayText>
                                        <Form>
                                            <Card sectioned>
                                                <FormLayout>
                                                    <p>The Header of your product pages is the most important section as itâ€™s the first thing a visitor sees. You have less than 3 seconds to get their attention to read more. You have to make it count!</p>
                                                    <p>Every product solves a problem. You need to identify what problem the product solves. Then you need to identify what someone is feeling who has this problem. (Sad, annoyed, frustrated, jealous)</p>
                                                    <p>Then you need the visitor to realize you solve that problem they have.</p>
                                                    <p>This is done in the headline. Asking if someone has that problem with a question is one of the best ways to hit the pain point someone has who needs your product.</p>
                                                    <FormLayout.Group>
                                                        <TextField
                                                            prefix="$"
                                                            value={price}
                                                            disabled={true}
                                                            label="Original price"
                                                            type="price"
                                                        />
                                                        <TextField
                                                            prefix="$"
                                                            value={discount}
                                                            onChange={this.handleChange('discount')}
                                                            label="Discounted price"
                                                            type="discount"
                                                        />
                                                    </FormLayout.Group>
                                                    <p>
                                                        This sale price will expire in two weeks
                                                    </p>
                                                </FormLayout>
                                            </Card>
                                            <PageActions
                                                primaryAction={[
                                                    {
                                                        content: 'Save',
                                                        onAction: () => {
                                                            const productVariableInput = {
                                                                id: variantId,
                                                                price: discount,
                                                            };
                                                            handleSubmit({
                                                                variables: { input: productVariableInput },
                                                            });
                                                        }
                                                    }
                                                ]}
                                                secondaryActions={[
                                                    {
                                                        content: 'Remove discount'
                                                    }
                                                ]}
                                            />
                                        </Form>
                                    </Layout.Section>
                                </Layout>
                            </Page>
                        </Frame>
                    );
                }}
            </Mutation>
        );
    }

    handleChange = (field) => {
        return (value) => this.setState({ [field]: value });
    };

    itemToBeConsumed = () => {
        const item = store.get('item');
        const price = item.variants.edges[0].node.price;
        const variantId = item.variants.edges[0].node.id;
        const discounter = price * 0.1;
        this.setState({ price, variantId });
        return (price - discounter).toFixed(2);
    };
}

export default EditProduct;
